def genlaugh():
    lg = ""
    parts = "–ê—Ö,–ê–•,–∞—Ö,–∞–•,–ê—ä,–ø—Ö,–ü—Ö,–ü–•,–ø–•,–∞–™,–ø–∑,".split(",")
    for x in range(randint(1, 20)):
        lg += choice(parts)
    return lg

def genscob():
    sc = ""
    scobs = "),0,-,_".split(",")
    for x in range(randint(1, 20)):
        sc += choice(scobs)
    return sc

