    insult += choice(smileys) * randint(0, 5)
    if choice([True, False]):
        insult += f" {choice(demonstrative_verbs)} {choice(places)}".upper() + choice(smileys) * randint(0, 6)
        if choice([True, False]):
            insult += f" {choice(insults)} {choice(adjectives)} {genlaugh()}".upper()
    return insult

